// This node extracts details from the bank SMS text.
// You MIGHT need to adjust the text patterns below to match YOUR bank's SMS format.

// --- ADJUST THESE PATTERNS IF NEEDED ---
// Tries to find amount like "Rs. 500.00" or "INR 500"
const amountRegex = /(?:Rs\.?|INR)\s*(\d+\.?\d*)/;
// Tries to find the merchant name after words like "at" or "to"
const merchantRegex = /(?:at|to)\s(.*?)\s*(?:\.|on|Avbl Bal)/;
// Tries to find a Transaction ID, often alphanumeric
const txIdRegex = /(?:Ref No|Txn ID)\s*([a-zA-Z0-9]+)/;
// --- END OF ADJUSTMENT AREA ---

return $input.all().map(item => {
  const smsText = item.json.text;
  const amountMatch = smsText ? smsText.match(amountRegex) : null;
  const merchantMatch = smsText ? smsText.match(merchantRegex) : null;
  const txIdMatch = smsText ? smsText.match(txIdRegex) : null;
  return {
    amount: amountMatch ? parseFloat(amountMatch[1]) : 'Not Found',
    merchant: merchantMatch ? merchantMatch[1].trim() : 'Not Found',
    transactionId: txIdMatch ? txIdMatch[1] : 'Not Found',
    originalText: smsText || 'No SMS Text Provided'
  };
});
